import React from "react";

import { useMutation, gql } from '@apollo/client';
import { Link } from 'react-router-dom';
//import client from './apollo'; // Import the Apollo Client instance

const REGISTER = gql`
  mutation Register($userId: String!, $passcode: String!, $emailId: String!) {
    register(userId: $userId, passcode: $passcode, emailId: $emailId) {
      id
      userId
      emailId
    }
  }
`;

const Register = () => {
  const [userId, setUserId] = useState('');
  const [passcode, setPasscode] = useState('');
  const [emailId, setEmailId] = useState('');

  const [register, { loading, error }] = useMutation(REGISTER, {
    client, // Use the Apollo Client instance
    onCompleted: ({ register }) => {
      // Handle successful registration
      console.log('Registered:', register);
      // Redirect or perform other actions
    },
  });

  const handleRegister = (e) => {
    e.preventDefault();
    register({ variables: { userId, passcode, emailId } });
  };

  return (
    <div className="auth">
      <h1>Register</h1>
      <form onSubmit={handleRegister}>
        <input
          required
          type="text"
          placeholder="UserId"
          value={userId}
          onChange={(e) => setUserId(e.target.value)}
        />
        <input
          required
          type="email"
          placeholder="Email"
          value={emailId}
          onChange={(e) => setEmailId(e.target.value)}
        />
        <input
          required
          type="password"
          placeholder="Password"
          value={passcode}
          onChange={(e) => setPasscode(e.target.value)}
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Registering...' : 'Register'}
        </button>
        {error && <p>Error: {error.message}</p>}
        <span>
          Already have an account? <Link to="/login">Login</Link>
        </span>
      </form>
    </div>
  );
};

export default Register;
