import React, { useState } from "react";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";
import { useMutation, gql } from '@apollo/client';
import useAuth from '/Users/aamiryaseen/blog-app/client/src/components/autth.js';

const CREATE_BLOG = gql`
  mutation createBlog($title: String!, $content: String!, $category: String!) {
    createBlog(title: $title, content: $content, category: $category) {
      blogId
      title
      content
      category
    }
  }
`;

const Write = () => {
  useAuth();

  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [category, setCategory] = useState('');

  const [createBlog, { loading, error }] = useMutation(CREATE_BLOG, {
    onCompleted: (data) => {
      // Handle successful creation, maybe redirect or show a success message
      console.log('Blog created:', data.createBlog);
    },
    onError: (error) => {
      // Handle error, maybe show an error message
      console.error('Mutation error:', error);
    },
  });

  const handlePublish = () => {
    // Call the mutation with variables
    createBlog({
      variables: {
        title: title,
        content: content,
        category: category,
      },
    });
  };

  return (
    <div className="add">
      <div className="content">
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <div className="editorContainer">
          <ReactQuill
            className="editor"
            theme="snow"
            value={content}
            onChange={(value) => setContent(value)}
          />
        </div>
      </div>
      <div className="menu">
        {/* ... (Other elements) */}
        <div className="item">
          <h1>Publish</h1>
          <span>
            <b>Status: </b> Draft
          </span>
          <span>
            <b>Visibility: </b> Public
          </span>
          <div className="buttons">
            <button onClick={handlePublish} disabled={loading}>
              {loading ? 'Saving...' : 'Save as a draft'}
            </button>
            <button onClick={handlePublish} disabled={loading}>
              {loading ? 'Publishing...' : 'Publish'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Write;
