import React, { useState } from 'react';
import { useMutation, gql } from '@apollo/client';

const AUTHENTICATE_USER = gql`
  mutation ValidateUser($userId: String!, $passcode: String!) {
    login(userId: $userId, passcode: $passcode) {
      token
    }
  }
`;

const Login = ({ history }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const [authenticateUser, { loading, error, data }] = useMutation(AUTHENTICATE_USER, {
    onCompleted: (result) => {
      const token = result.login.token;
      if (token) {
        localStorage.setItem('token', token);
        history.push('/');
      }
    },
  });

  const handleLogin = (e) => {
    e.preventDefault();

    // Call the mutation with variables
    authenticateUser({
      variables: {
        userId: username,
        passcode: password,
      },
    });
  };

  return (
    <div className="auth">
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <label>
          Username:
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </label>
        <br />
        <button type="submit" disabled={loading}>
          {loading ? 'Logging in...' : 'Login'}
        </button>
        {error && <p>Error: {error.message}</p>}
      </form>
    </div>
  );
};

export default Login;
