  query GetBlog($blogId: ID!) {
    getBlog(blogId: $blogId) {
      blogId
      blogDetails
      blogTitle
    }
  }
`;

const Single = ({ blogId }) => {
  const { loading, error, data } = useQuery(GET_BLOG, {
